mapper.select:
SELECT
	if(COUNT(0)=0, '0001', LPAD(cast(substring(MAX(oi.order_num), 11, 14) as decimal)+1,4,0))
FROM
	order_info AS oi
	where
		oi.order_reg_date = CURDATE();

mapper.update:
UPDATE order_info
SET
	payment_check='Y',
	order_modify_date=CURDATE()
WHERE member_id=#{memberId} 
and payment_check='N';
